package com.kuaixiu.card.entity;import com.common.base.entity.BaseEntity;/** * . * * @CreateDate: 2018-12-27 下午03:29:34 * @version: V 1.0 */public class CardType extends BaseEntity {        /**     * 序列化Id     */    private static final long serialVersionUID = 1L;    /**     *      */    private Long id ;    /**     * 号卡区分  0：类别  1：名称     */    private Integer type ;    /**     * 号卡编号     */    private Long cardNo ;    /**     * 号卡名字     */    private String cardName ;    /**     * get:     */    public Long getId(){        return this.id;    }    /**     * set：     */    public void setId(Long id){        this.id=id;    }    /**     * get:号卡区分  0：类别  1：名称     */    public Integer getType(){        return this.type;    }    /**     * set：号卡区分  0：类别  1：名称     */    public void setType(Integer type){        this.type=type;    }    /**     * get:号卡编号     */    public Long getCardNo(){        return this.cardNo;    }    /**     * set：号卡编号     */    public void setCardNo(Long cardNo){        this.cardNo=cardNo;    }    /**     * get:号卡名字     */    public String getCardName(){        return this.cardName;    }    /**     * set：号卡名字     */    public void setCardName(String cardName){        this.cardName=cardName;    }}