mapper.insert:
insert into kx_recycle_checkitems
id, wechat_id, brand, wechat_model, recycle_model, product_id, in_time,last_price,brand_id,model_id,mobile
,login_mobile
uuid(), #{wechatId,jdbcType=VARCHAR}, #{brand,jdbcType=VARCHAR}, #{wechatModel,jdbcType=VARCHAR}, #{recycleModel,jdbcType=VARCHAR}, #{productId,jdbcType=VARCHAR}, now(), #{lastPrice,jdbcType=DECIMAL},#{brandId,jdbcType=VARCHAR},#{modelId,jdbcType=VARCHAR}, #{mobile,jdbcType=VARCHAR}
,#{loginMobile,jdbcType=VARCHAR}

mapper.select:
select

from kx_recycle_checkitems
where 1=1

    and id = #{id}


    and wechat_id=#{wechatId}


    and wechat_model =#{wechatModel}


    and recycle_model =#{recycleModel}


    and mobile =#{mobile}


    and login_mobile =#{loginMobile}

order by in_time desc

