mapper.update:
update hs_coupon
set 
    is_del=1,
    update_time=now(),
    update_userid=#{updateUserid}
where batch_id=#{batchId}

update hs_coupon
set
is_del=#{isDel},
update_time=now(),
update_userid=#{updateUserid}
where id = #{id}

mapper.select:
select count(1)
from hs_coupon t
where 1=1

    and t.id = #{id}


    and t.batch_id = #{batchId}


    and t.coupon_code = #{couponCode}


    and t.coupon_name = #{couponName}


    and t.coupon_price = #{strCouponPrice}


    and t.begin_time = #{beginTime}


    and t.end_time = #{endTime}


    and t.status = #{status}


    and t.is_use = #{isUse}


    and t.is_receive = #{isReceive}


    and t.is_del=#{isDel}

select

from hs_coupon t
where 1=1

    and t.id = #{id}


    and t.batch_id = #{batchId}


    and t.coupon_code = #{couponCode}


    and t.coupon_name = #{couponName}


    and t.coupon_price = #{strCouponPrice}


    and t.begin_time = #{beginTime}


    and t.end_time = #{endTime}


    and t.status = #{status}


    and t.is_use = #{isUse}


    and t.is_receive = #{isReceive}


    and t.receive_mobile=#{receiveMobile}


    and t.is_del=#{isDel}

select

from hs_coupon t
where 1=1

    and t.id = #{id}


    and t.batch_id = #{batchId}


    and t.coupon_code like CONCAT('%', #{couponCode}, '%')


    and t.coupon_name = #{couponName}


    and t.coupon_price = #{strCouponPrice}


    and t.begin_time = #{beginTime}


    and t.end_time = #{endTime}


    and t.status = #{status}


    and t.is_use = #{isUse}


    and t.is_receive = #{isReceive}


    and t.is_del=#{isDel}

order by t.create_time desc,t.coupon_code

select

from hs_coupon t
where 1=1

    and t.id = #{id}


    and t.batch_id = #{batchId}


    and t.coupon_code = #{couponCode}


    and t.coupon_name = #{couponName}


    and t.coupon_price = #{strCouponPrice}


    and t.begin_time <= #{beginTime}


    and t.end_time >= #{endTime}


    and t.status = #{status}


    and t.is_use = #{isUse}


    and t.is_receive = #{isReceive}


    and t.receive_mobile=#{receiveMobile}


    and t.is_del=#{isDel}

order BY end_time
