mapper.select:
select

from sj_customer_detail
where 1=1

    and id = #{id}


    and login_id = #{loginId}


    and city_company_id = #{cityCompanyId}


    and management_unit_id = #{managementUnitId}


    and branch_office_id = #{branchOfficeId}


    and contract_body_id = #{contractBodyId}


    and marketing_no = #{marketingNo}

select

from sj_customer_detail
where 1=1

    and id = #{id}


    and login_id = #{loginId}


    and city_company_id = #{cityCompanyId}


    and management_unit_id = #{managementUnitId}


    and branch_office_id = #{branchOfficeId}


    and contract_body_id = #{contractBodyId}


    and marketing_no = #{marketingNo}

select u.login_id,u.name,u.phone,c.city_company_id,c.management_unit_id,c.branch_office_id, c.contract_body_id, if(c.contract_body_id is null,if(c.branch_office_id is null,if(c.management_unit_id is null, ac.city_company,am.management_unit),ab.branch_office),ao.contract_body) type_name, c.marketing_no,date_format(u.`create_time`,'%Y-%m-%d %H:%i:%s')create_time,u.is_cancel from sj_customer_detail c LEFT JOIN sj_user u ON c.login_id=u.id LEFT JOIN sj_area_city_company ac ON ac.id=c.city_company_id LEFT JOIN sj_area_management_unit am ON am.id=c.management_unit_id LEFT JOIN sj_area_branch_office ab ON ab.id=c.branch_office_id LEFT JOIN sj_area_contract_body ao ON ao.id=c.contract_body_id where 1=1 and u.is_del=0
and u.`phone` like CONCAT('%', #{phone}, '%')
and u.`name` like CONCAT('%', #{name}, '%')
and u.create_time >= #{queryStartTime}
and u.create_time <= #{queryEndTime}
and c.city_company_id is not null and c.management_unit_id is null
and c.management_unit_id is not null and c.branch_office_id is null
and c.branch_office_id is not null and c.contract_body_id is null
and c.contract_body_id is not null
and c.marketing_no = #{marketingNo}
